import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { GiHamburgerMenu } from 'react-icons/gi';
import { ImCross } from "react-icons/im";
import images from '../../constants/images';
import './Navbar.css';
// 'Gallery'

const Navbar = () => {
  const [toggleMenu, setToggleMenu] = useState(false);
  const [isFixed, setIsFixed] = useState(false);

  const location = useLocation(); // To detect current URL
  const navigate = useNavigate(); // To programmatically navigate

  // Add fixed navbar on scroll
  const handleScroll = () => {
    setIsFixed(window.scrollY > 100);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Function to handle navigation to homepage
  const handleLogoClick = () => {
    if (location.pathname !== '/') {
      navigate('/');
    } else {
      const section = document.querySelector('#home');
      section?.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Function to handle scroll or redirect
  const handleNavigation = (id) => {
    if (location.pathname === '/') {
      // On the homepage, scroll smoothly
      const section = document.querySelector(id);
      if (section) {
        const offsetTop = section.getBoundingClientRect().top + window.scrollY; // Get the top position of the section
        const adjustedPosition = offsetTop - 100; // Adjust by 100px to account for the navbar height
        window.scrollTo({ top: adjustedPosition, behavior: 'smooth' });
      }
    } else {
      // On other pages, navigate to the homepage and then scroll
      navigate('/');
      setTimeout(() => {
        const section = document.querySelector(id);
        if (section) {
          const offsetTop = section.getBoundingClientRect().top + window.scrollY;
          const adjustedPosition = offsetTop - 100; // Adjust by 100px
          window.scrollTo({ top: adjustedPosition, behavior: 'smooth' });
        }
      }, 100); // Delay to ensure navigation is complete
    }
  };

  return (
    <nav
      className={`z-nav ${isFixed ? 'shadow-md' : ''} w-full flex justify-between items-center bg-white sm:px-8 sm:py-4 px-4 py-2`}
    >
      {/* Logo */}
      <div className="flex items-center pl-32 cursor-pointer" onClick={handleLogoClick}>
        <img
          src={images.cinegraphics}
          alt="app logo"
          className="h-auto w-24 sm:w-40 2xl:w-24 no-margin"
          style={{ marginTop: 0 }} // Inline style to remove top margin
        />
      </div>

      {/* Desktop Navigation */}
      <ul className="hidden lg:flex justify-center items-center pr-32 gap-20">
        {['Home', 'Projects', 'About Us', 'Contact'].map((item, index) => (
          <li
            key={index}
            className="text-primary-blue font-semibold hover:text-primary-orange cursor-pointer"
            onClick={() => handleNavigation(`#${item.toLowerCase().replace(/\s+/g, '')}`)}
          >
            {item}
          </li>
        ))}
      </ul>

      {/* Hamburger Menu for Mobile */}
      <div className="lg:hidden">
        <GiHamburgerMenu
          color="#000"
          fontSize={27}
          onClick={() => setToggleMenu(true)}
        />
        {toggleMenu && (
          <div className="slide-bottom fixed top-0 left-0 w-full h-screen bg-primary-black text-white flex flex-col items-center z-50">
            <ImCross
              className="absolute top-5 right-5 cursor-pointer"
              color="#fff"
              fontSize={27}
              onClick={() => setToggleMenu(false)}
            />
            <ul className="list-none mt-20 space-y-8">
              {['Home', 'Projects', 'Gallery', 'About Us', 'Contact'].map((item, index) => (
                <li
                  key={index}
                  className="text-2xl font-bold cursor-pointer hover:text-primary-orange"
                  onClick={() => {
                    handleNavigation(`#${item.toLowerCase().replace(/\s+/g, '')}`);
                    setToggleMenu(false); // Close menu after click
                  }}
                >
                  {item}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
