import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaChevronLeft, FaReact, FaUserShield, FaChevronRight } from "react-icons/fa";
import { MdPark } from "react-icons/md";
import { BsFillHousesFill, BsBank2 } from "react-icons/bs";
import { GiElectric, GiWaterTank } from "react-icons/gi";
import { CgFileDocument } from "react-icons/cg";
import project1a from '../assets/project1/Project1.1.JPG';
import project1b from '../assets/project1/Project1.2.JPG';
import project1c from '../assets/project1/project1.3.JPG';
import project1d from '../assets/project1/Project1.4.JPG';
import project1e from '../assets/project1/Project1.5.JPG';
import "./Projectpage.css";

const PropertyView = () => {
  const [currentImage, setCurrentImage] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const images = [
    { url: project1a, alt: "Modern luxury house exterior" },
    { url: project1b, alt: "Contemporary living room interior" },
    { url: project1c, alt: "Elegant kitchen design" },
    { url: project1d, alt: "Master bedroom suite" },
    { url: project1e, alt: "Luxurious bathroom" }
  ];

  const navigate = useNavigate();

  const handleContactClick = () => {
    navigate("/contactus");
  };

  const amenities = [
    { icon: <FaReact />, name: "100% Vaasthu" },
    { icon: <BsFillHousesFill />, name: "Gated Community" },
    { icon: <CgFileDocument />, name: "Spot Registration" },
    { icon: <GiElectric />, name: "Underground Electricity" },
    { icon: <MdPark />, name: "Garden Area" },
    { icon: <FaUserShield />, name: "24/7 Security" },
    { icon: <GiWaterTank />, name: "Overhead Tanks" },
    { icon: <BsBank2 />, name: "Bank Loan Facility" }
  ];

  useEffect(() => {
    // Scroll to the top of the page when the component is loaded
    window.scrollTo(0, 0);

    const loadImages = async () => {
      try {
        setIsLoading(true);
        await new Promise(resolve => setTimeout(resolve, 1000));
        setIsLoading(false);
      } catch (err) {
        setError("Failed to load images. Please try again later.");
        setIsLoading(false);
      }
    };
    loadImages();
  }, []);

  const nextImage = () => {
    setCurrentImage((prev) => (prev + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentImage((prev) => (prev - 1 + images.length) % images.length);
  };

  if (error) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-red-500 text-xl">{error}</div>
      </div>
    );
  }

  return (
    <div className="mainpage max-w-8xl  mt-20 px-4 py-8">
      {/* Image Carousel */}
      <div className="relative rounded-lg shadow-lg mb-8">
        {isLoading ? (
          <div className="h-[600px] bg-gray-200 animate-pulse flex items-center justify-center">
            <div className="text-gray-400">Loading...</div>
          </div>
        ) : (
          <div className="mainimg relative ">
            <img
              src={`${images[currentImage].url}`}
              alt={images[currentImage].alt}
              className="w-full h-full rounded-xl object-cover transition-opacity duration-500"
            />
            {/* Left Chevron */}
            <button
              onClick={prevImage}
              className="chevron absolute left-4 top-1/2 transform -translate-y-1/2 p-2 pb-2 bg-white/80 rounded-full hover:bg-white transition-colors"
              aria-label="Previous image"
            >
              <FaChevronLeft className="carr text-gray-800 text-xl" />
            </button>
            {/* Right Chevron */}
            <button
              onClick={nextImage}
              className="chevron absolute right-4 top-1/2 transform -translate-y-1/2 p-2 pl-3 bg-white/80 rounded-full hover:bg-white transition-colors"
              aria-label="Next image"
            >
              <FaChevronRight className="carr text-gray-800 text-xl" />
            </button>
            {/* Image Tabs */}
            <div className="absolute bottom-4 left-0 right-0 flex justify-center gap-2">
              {images.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentImage(index)}
                  className={`w-2 h-2 rounded-full transition-all duration-300 ${currentImage === index ? "bg-white w-4" : "bg-white/50"}`}
                  aria-label={`Go to image ${index + 1}`}
                />
              ))}
            </div>
          </div>
        )}
      </div>

      <section className="mb-12">
        <h1 className="text-4xl text-primary-black font-bold mb-6 flex items-center gap-2">
          Eastern Meadows
        </h1>
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-2xl font-semibold mb-4">Project Overview</h2>
          <p className="text-gray-600 leading-relaxed">
            Experience luxury living at its finest in our newest residential development. Featuring modern architecture and premium finishes, these homes offer the perfect blend of comfort and sophistication.
          </p>
        </div>
      </section>

      <section className="mb-12">
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-2xl font-semibold mb-6">Amenities</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
            {amenities.map((amenity, index) => (
              <div key={index} className="flex items-center gap-3 p-4 bg-gray-50 rounded-lg">
                <span className="text-blue-600 text-xl">{amenity.icon}</span>
                <span className="font-medium">{amenity.name}</span>
              </div>
            ))}
          </div>
        </div>
      </section>

      <section className="mb-12">
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-2xl font-semibold mb-4">Project Details</h2>
          <div className="space-y-4 text-gray-600">
            <p>
              Experience luxury living in this stunning modern residence. This meticulously designed home features open-concept living spaces, premium finishes, and abundant natural light. The gourmet kitchen boasts high-end appliances and custom cabinetry. The primary suite includes a spa-like bathroom and walk-in closet. Additional features include a home office, media room, and beautifully landscaped backyard perfect for entertaining.
            </p>
            <ul className="list-disc pl-6 space-y-2">
              <li>Plot Area - 150 SQ.Yd</li>
              <li>Ground Floor Area - 1000 SQ.FT</li>
              <li>Total Area - 29,254.54 SQ.YD</li>
              <li>Plotted Area - 20,445.49 SQ.YD</li>
            </ul>
          </div>
        </div>
      </section>

      <section className="mb-12">
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-2xl font-semibold mb-4">Location</h2>
          <div className="aspect-w-16 aspect-h-9">
            <iframe
              src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d30469.14175789527!2d78.67809811682545!3d17.332779196863495!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3bcb0ad9eb8f38f1%3A0x70aa6afb83d8dd5f!2sKawadipally%2C%20Telangana!5e0!3m2!1sen!2sin!4v1732706231784!5m2!1sen!2sin"
              className="w-full h-[400px] rounded-lg"
              style={{ border: 0 }}
              allowFullScreen=""
              loading="lazy"
              title="Project location map"
            ></iframe>
          </div>
          <div className="mt-6">
            <p className="font-semibold mb-2">Sy.Nos.106/P,107/P,108/P, Kawadiapally, Abdullapurmet, Ranga Reddy Dist.</p>
            <div className="text-gray-700 space-y-2 mb-4">
              <p>• 2KM from Vijayawada Highwway</p>
              <p>• 4KM from ORR exit 10</p>
              <p>• 18KM from LB Nagar</p>
              <p>• Easy Highway Access</p>
            </div>
          </div>
        </div>
      </section>

      <div className="fixed bottom-8 right-8">
        <button
          onClick={handleContactClick}
          className="conbut bg-blue-600 text-white rounded-full shadow-lg hover:bg-blue-700 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
        >
          Contact Us
        </button>
      </div>
    </div>
  );
};

export default PropertyView;
