import React, { useState } from "react";
import arrow2 from "../../assets/arrow2.png";
import { FaXTwitter } from "react-icons/fa6";
import axios from "axios";
import './ContactUs.css';
import { FaFacebook, FaInstagram } from "react-icons/fa";

const ContactUs = () => {
  const [Values, setValues] = useState({
    name: "",
    phone: "",
    email: "",
    subject: "",
  });
  const [errors, setErrors] = useState({});
  const [submitted, setSubmitted] = useState(false);

  const subjectOptions = [
    { value: "easternmeaadows", label: "Eastern Meadows" },
    { value: "sricounty", label: "Sri County Phase-II" },
    { value: "easternmeadows1", label: "Eastern Meadows-I" },
    { value: "other", label: "Other" },
  ];

  const change = (e) => {
    const { name, value } = e.target;
    setValues({ ...Values, [name]: value });
  };

  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  const validatePhone = (phone) => /^\+?\d{10,15}$/.test(phone);

  const submit = async (e) => {
    e.preventDefault();

    const newErrors = {};
    if (!Values.name) newErrors.name = "Name is required.";
    if (!Values.email) {
      newErrors.email = "Email is required.";
    } else if (!validateEmail(Values.email)) {
      newErrors.email = "Enter a valid email address.";
    }
    if (!Values.phone) {
      newErrors.phone = "Phone number is required.";
    } else if (!validatePhone(Values.phone)) {
      newErrors.phone = "Enter a valid phone number.";
    }
    if (!Values.subject) newErrors.subject = "Subject is required.";

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    try {
      const res = await axios.post("https://srimaatha-test.onrender.com/api/v1/post", Values);
      alert(res.data.message);
      setSubmitted(true);
      setValues({
        name: "",
        phone: "",
        email: "",
        subject: "",
      });
      setErrors({});
    } catch (error) {
      const errorMsg =
        error.response?.data?.message || "An unexpected error occurred.";
      alert(errorMsg);
    }
  };

  return (
    <div
      className="min-h-screen bg-primary-blue py-10 px-4 sm:px-6 lg:px-8"
      id="contact"
    >
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="context text-5xl flex gap-4 font-bold text-white mb-0 mt-4">
            Contact Us
            <img src={arrow2} alt="arrow" className="h-6 mt-5" />
          </h1>

        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          <div className="bg-white rounded-lg shadow-lg p-8">
            {submitted ? (
              <div className="text-center py-12">
                <h2 className="text-2xl font-semibold text-green-600 mb-4">
                  Thank you for your message!
                </h2>
                <p className="text-gray-600">We'll get back to you soon.</p>
                <button
                  onClick={() => setSubmitted(false)}
                  className="mt-6 bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 transition-colors"
                >
                  Send another message
                </button>
              </div>
            ) : (
              <form onSubmit={submit} className="space-y-6">
                {/* Name */}
                <div>
                  <label
                    htmlFor="name"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Name
                  </label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={Values.name}
                    onChange={change}
                    className={`mt-1 block w-full rounded-md shadow-sm ${
                      errors.name
                        ? "border-red-500 focus:border-red-500 focus:ring-red-500"
                        : "border-gray-300 focus:border-blue-500 focus:ring-blue-500"
                    }`}
                    aria-label="Name"
                  />
                  {errors.name && (
                    <p className="mt-1 text-sm text-red-600">{errors.name}</p>
                  )}
                </div>

                {/* Email */}
                <div>
                  <label
                    htmlFor="email"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Email
                  </label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={Values.email}
                    onChange={change}
                    className={`mt-1 block w-full rounded-md shadow-sm ${
                      errors.email
                        ? "border-red-500 focus:border-red-500 focus:ring-red-500"
                        : "border-gray-300 focus:border-blue-500 focus:ring-blue-500"
                    }`}
                    aria-label="Email"
                  />
                  {errors.email && (
                    <p className="mt-1 text-sm text-red-600">{errors.email}</p>
                  )}
                </div>

                {/* Phone */}
                <div>
                  <label
                    htmlFor="phone"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Phone Number
                  </label>
                  <input
                    type="tel"
                    id="phone"
                    name="phone"
                    value={Values.phone}
                    onChange={change}
                    placeholder="+91"
                    className={`mt-1 block w-full rounded-md shadow-sm ${
                      errors.phone
                        ? "border-red-500 focus:border-red-500 focus:ring-red-500"
                        : "border-gray-300 focus:border-blue-500 focus:ring-blue-500"
                    }`}
                    aria-label="Phone Number"
                  />
                  {errors.phone && (
                    <p className="mt-1 text-sm text-red-600">{errors.phone}</p>
                  )}
                </div>

                {/* Subject */}
                <div>
                  <label
                    htmlFor="subject"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Interested in
                  </label>
                  <select
                    id="subject"
                    name="subject"
                    value={Values.subject}
                    onChange={change}
                    className={`mt-1 block w-full rounded-md shadow-sm ${
                      errors.subject
                        ? "border-red-500 focus:border-red-500 focus:ring-red-500"
                        : "border-gray-300 focus:border-blue-500 focus:ring-blue-500"
                    }`}
                    aria-label="Subject"
                  >
                    {subjectOptions.map((option) => (
                      <option key={option.value} value={option.value}>
                        {option.label}
                      </option>
                    ))}
                  </select>
                  {errors.subject && (
                    <p className="mt-1 text-sm text-red-600">
                      {errors.subject}
                    </p>
                  )}
                </div>

                <button
                  type="submit"
                  className="message w-full bg-primary-orange text-white  pb-2   rounded-xl hover:bg-primary-blue transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
                >
                  Send Message
                </button>
                <p className="text-green-700">
                  Select the venture that you are interested in and fill in your details. Our team will reach out to you as soon as possible.
                </p>
              </form>
            )}
          </div>

          {/* Other Content */}
          <div className="space-y-8">
            <div className="bg-white rounded-lg shadow-lg p-8">
              <h2 className="text-2xl font-semibold text-gray-900 mb-6">
                Our Location
              </h2>
              <div className="bg-gray-100 h-96 rounded-lg flex items-center justify-center">
                <iframe
                  src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3808.0779154849124!2d78.54213957591008!3d17.359983403592487!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3bcb99255f009079%3A0xf6136c63be907dce!2sSri%20Maatha%20Infra%20Developers!5e0!3m2!1sen!2sin!4v1732105322953!5m2!1sen!2sin"
                  width="100%"
                  height="400"
                  style={{ border: 0 }}
                  allowFullScreen=""
                  loading="lazy"
                  referrerPolicy="no-referrer-when-downgrade"
                  title="Our Location"
                  className="rounded-lg"
                ></iframe>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow-lg p-8">
              <h2 className="text-2xl font-semibold text-gray-900 mb-6">
                Follow Us
              </h2>
              <div className="flex gap-4">
                <FaFacebook
                  size={32}
                  className="cursor-pointer text-blue-600 hover:scale-110 transition-transform"
                />
                <FaXTwitter
                  size={32}
                  className="cursor-pointer text-gray-700 hover:scale-110 transition-transform"
                />
                <FaInstagram
                  size={32}
                  className="cursor-pointer text-pink-500 hover:scale-110 transition-transform"
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ContactUs;
