import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import "./Projects.css";
import { FiArrowRight } from "react-icons/fi";
import { BiLoaderAlt } from "react-icons/bi";
import arrow2 from '../../assets/arrow2.png';
import project1 from '../../assets/Project1.1.JPG';
import project2 from '../../assets/project2.1.JPG';
import project3 from '../../assets/project3.1.JPG';

const Projects = () => {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate(); // Initialize useNavigate

  const cardData = [
    {
      id: 1,
      title: "Eastern Meadows",
      description: "Premium Villa and open plots.",
      image: project1,
      cta: "Explore",
      route: "/Projectpage1" // Add a route for navigation
    },
    {
      id: 2,
      title: "Sri County Phase-II",
      description: "Premium Villa and open plots.",
      image: project2,
      cta: "Explore",
      route: "/Projectpage2"
    },
    {
      id: 3,
      title: "Eastern Meadows - I",
      description: "Premium Villa and open plots.",
      image: project3,
      cta: "Explore",
      route: "/Projectpage3"
    }
  ];

  const handleClick = (route) => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      navigate(route); // Navigate to the specific route
    }, 1500);
  };

  return (
    <div className="page w-full h-screen-big bg-gray-50 px-4 pt-10 sm:px-6 lg:px-8" id="projects">
      <h1 className="title font-bold text-primary-blue mb-10  flex gap-4">
        Our Projects
        <img src={arrow2} alt='arrow' className='' />
      </h1>
      <div className="projects mx-auto">
        {loading && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <BiLoaderAlt className="animate-spin text-white text-4xl" />
          </div>
        )}
        <div className="space-y-8">
          {cardData.map((card) => (
            <div
              key={card.id}
              className="bg-white rounded-xl shadow-lg overflow-hidden transition-transform duration-300 hover:scale-[1.02] focus-within:ring-2 focus-within:ring-blue-500"
              role="article"
            >
              <div className="flex flex-col">
                <div className="proimg w-full px-8 pt-4 relative">

                <img
                   onClick={() => handleClick(card.route)}
                    src={`${card.image}`}
                    alt={card.title}
                    className="w-full h-full object-cover cursor-pointer rounded-lg"
                  />




                </div>
                <div className="p-6 md:p-8">
                  <div className="cardcon">
                    <div className="flex flex-col justify-between">
                    <h2
                      className="title-text  flex justify-between font-bold text-primary-blue "
                      id={`card-title-${card.id}`}
                    >
                      {card.title}


                    </h2>
                    <p
                      className="text-gray-600 mb-4 mt-1"
                      id={`card-desc-${card.id}`}
                    >
                      {card.description}
                    </p>
                    <button
                        onClick={() => handleClick(card.route)} // Navigate to specific route
                        className="inline-flex items-center  justify-center pl-3 w-32 h-10 border border-transparent text-base font-medium rounded-3xl text-white bg-primary-orange hover:bg-primary-blue focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors duration-200"
                        aria-labelledby={`card-title-${card.id}`}
                        aria-describedby={`card-desc-${card.id}`}
                      >
                        {card.cta}
                        <FiArrowRight className="ml-2" aria-hidden="true" />
                      </button>
                    </div>


                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Projects;
