import React, { useState, useRef, useEffect } from "react";
import walkthrough from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/walkthrough.jpg';  
import interior from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/interior.jpg';
import creative from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/creative.jpg';
import graphic from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/graphic.jpg';
import game from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/game.jpg';
import motion from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/motion.jpg';
import digital from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/digital.jpg';

const dummyProducts = [
  {
    id: 1,
    title: "Walkthrough & Cinematics",
    image: walkthrough
  },
  {
    id: 2,
    title: "Interior Design",
    image: interior
  },
  {
    id: 3,
    title: "Creative Visualization",
    image: creative
  },
  {
    id: 4,
    title: "Graphic Design",
    image: graphic
  },
  {
    id: 5,
    title: "Game Development",
    image: game
  },
  {
    id: 6,
    title: "Motion Graphics",
    image: motion
  },
  {
    id: 7,
    title: "Digital Marketing",
    image: digital
  }
];

const ProductCard = ({ product }) => {
  return (
    <div className="flex-shrink-0 w-96 bg-transparent rounded-lg overflow-visible transform transition-all duration-300 hover:scale-105 mx-4">
      <div className="relative h-80 overflow-hidden">
        <img
          src={product.image}
          alt={product.title}
          className="w-full h-full object-cover transition-transform duration-300 hover:scale-110"
          onError={(e) => {
            e.target.src = "https://images.unsplash.com/photo-1590212151175-e58edd96185b";
          }}
        />
        {/* Text and button overlay */}
        <div className="absolute inset-0  flex flex-col justify-start items-center text-center">
          <h3 className="text-xl font-semibold text-white mb-4">{product.title}</h3>
          <button className="px-4 py-2 bg-primary-orange text-white hover:bg-orange-500 transition-colors duration-300">
            Explore
          </button>
        </div>
      </div>
    </div>
  );
};


const Services = () => {
  const containerRef = useRef(null);
  const autoScrollRef = useRef();

  const CARD_WIDTH = 288; // Width of a single card
  const SCROLL_AMOUNT = CARD_WIDTH * 2; // Scroll 2 cards at a time

  const startAutoScroll = () => {
    autoScrollRef.current = setInterval(() => {
      if (containerRef.current) {
        const container = containerRef.current;

        // Smooth scroll by the width of 2 cards
        container.scrollTo({
          left: container.scrollLeft + SCROLL_AMOUNT,
          behavior: "smooth",
        });

        // Reset scroll position if the end is reached
        if (container.scrollLeft >= container.scrollWidth / 2) {
          setTimeout(() => {
            container.scrollLeft = 0;
          }, 500); // Allow time for smooth scroll to complete
        }
      }
    }, 4000); // Adjust interval for periodic scroll
  };

  useEffect(() => {
    startAutoScroll(); // Start auto-scroll on mount
    return () => clearInterval(autoScrollRef.current); // Cleanup on unmount
  }, []);

  const handleMouseDown = (e) => {
    clearInterval(autoScrollRef.current); // Stop auto-scroll during drag
  };

  const handleMouseUp = () => {
    startAutoScroll(); // Resume auto-scroll after drag
  };

  return (
    <div className="relative overflow-hidden bg-gray-100 py-8">
      <div className="w-[85vw] ml-[9em] ">
        <h2 className="services-heading text-6xl font-nunito text-center text-black mb-24">OUR SERVICES</h2>
        <div
          ref={containerRef}
          className="relative flex overflow-hidden"
          onMouseDown={handleMouseDown}
          onMouseUp={handleMouseUp}
        >
          <div className="flex">
            {/* Render the cards twice for seamless looping */}
            {dummyProducts.concat(dummyProducts).map((product, index) => (
              <ProductCard key={index} product={product} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Services;
