import { useState } from "react";
import { FiMaximize2, FiX } from "react-icons/fi";

const Interior = () => {
  const [selectedImage, setSelectedImage] = useState(null);

  const galleryItems = [
    {
      id: 1,
      title: "Mountain Lake",
      description: "Serene mountain lake during sunset",
      imageUrl: "https://images.unsplash.com/photo-1506905925346-21bda4d32df4",
      span: "col-span-2 row-span-2"
    },
    {
      id: 2,
      title: "Desert Landscape",
      description: "Vast desert with rolling sand dunes",
      imageUrl: "https://images.unsplash.com/photo-1682686580003-82686c00f758",
      span: "col-span-1 row-span-1"
    },
    {
      id: 3,
      title: "Ocean Waves",
      description: "Powerful waves crashing on rocky shore",
      imageUrl: "https://images.unsplash.com/photo-1682687220742-aba13b6e50ba",
      span: "col-span-1 row-span-1"
    },
    {
      id: 4,
      title: "Forest Path",
      description: "Misty morning in dense forest",
      imageUrl: "https://images.unsplash.com/photo-1682687221175-9135f1217814",
      span: "col-span-1 row-span-2"
    },
    {
      id: 5,
      title: "City Lights",
      description: "Urban nightscape with vibrant lights",
      imageUrl: "https://images.unsplash.com/photo-1682687220199-d0124f48f95b",
      span: "col-span-2 row-span-1"
    },
    {
      id: 6,
      title: "Autumn Colors",
      description: "Vibrant fall foliage in nature",
      imageUrl: "https://images.unsplash.com/photo-1682687221080-5cb261c645cb",
      span: "col-span-1 row-span-1"
    }
  ];

  const handleImageClick = (image) => {
    setSelectedImage(image);
  };

  const handleCloseLightbox = () => {
    setSelectedImage(null);
  };

  return (
    <div className="container mx-[8.5em] mt-32 px-4 py-8">
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4 auto-rows-[400px]">
        {galleryItems.map((item) => (
          <div
            key={item.id}
            className={`${item.span} relative group overflow-hidden rounded-xl cursor-pointer`}
            onClick={() => handleImageClick(item)}
          >
            <img
              src={item.imageUrl}
              alt={item.title}
              className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-110"
              onError={(e) => {
                e.target.src = "https://images.unsplash.com/photo-1682686580186-b55d2a91053c";
                e.target.alt = "Fallback Image";
              }}
            />
            <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-40 transition-all duration-300">
              <div className="absolute bottom-0 left-0 right-0 p-4 text-white transform translate-y-full group-hover:translate-y-0 transition-transform duration-300">
                <h3 className="text-lg font-semibold">{item.title}</h3>
                <p className="text-sm">{item.description}</p>
              </div>
              <FiMaximize2 className="absolute top-4 right-4 text-white opacity-0 group-hover:opacity-100 transition-opacity duration-300" size={24} />
            </div>
          </div>
        ))}
      </div>

      {selectedImage && (
        <div className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50">
          <div className="relative max-w-4xl w-full mx-4">
            <button
              className="absolute top-4 right-4 text-white hover:text-gray-300 transition-colors duration-300"
              onClick={handleCloseLightbox}
            >
              <FiX size={24} />
            </button>
            <img
              src={selectedImage.imageUrl}
              alt={selectedImage.title}
              className="w-full h-auto rounded-lg"
              onError={(e) => {
                e.target.src = "https://images.unsplash.com/photo-1682686580186-b55d2a91053c";
                e.target.alt = "Fallback Image";
              }}
            />
            <div className="mt-4 text-white">
              <h2 className="text-2xl font-bold">{selectedImage.title}</h2>
              <p className="mt-2">{selectedImage.description}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Interior;
