import { useState, useEffect, useCallback } from "react";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";
import game1a from "C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/game/bubblefish1.jpeg";
import game2a from "C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/game/bubblefish2.jpeg";
import game3a from "C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/game/bubblefish3.jpeg";
import game1b from "C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/game/volcano1.jpeg";
import game2b from "C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/game/volcano2.jpeg";
import game3b from "C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/game/volcano3.jpeg";

const Game = () => {
  const images1 = [
    { url: game1a, title: "Bubble Fish" },
    { url: game2a, title: "Bubble Fish" },
    { url: game3a, title: "Bubble Fish" },
  ];

  const images2 = [
    { url: game1b, title: "Volcano Run" },
    { url: game2b, title: "Volcano Run" },
    { url: game3b, title: "Volcano Run" },
  ];

  const [currentIndex1, setCurrentIndex1] = useState(0);
  const [currentIndex2, setCurrentIndex2] = useState(0);

  const handleSlideChange = (indexSetter, length) => (direction) => {
    indexSetter((prevIndex) =>
      direction === "next"
        ? (prevIndex + 1) % length
        : (prevIndex - 1 + length) % length
    );
  };

  const autoSlide = (indexSetter, length) => {
    return useCallback(() => {
      indexSetter((prevIndex) => (prevIndex + 1) % length);
    }, [indexSetter, length]);
  };

  useEffect(() => {
    const interval1 = setInterval(autoSlide(setCurrentIndex1, images1.length), 3000);
    const interval2 = setInterval(autoSlide(setCurrentIndex2, images2.length), 3000);

    return () => {
      clearInterval(interval1);
      clearInterval(interval2);
    };
  }, [autoSlide, images1.length, images2.length]);

  const renderCarousel = (images, currentIndex, setCurrentIndex) => {
    return (
      <div className="relative max-w-3xl mt-2 mx-0">
        <div
          className="relative h-[400px] overflow-hidden rounded-lg"
          role="region"
          aria-label="Image carousel"
        >
          <div
            className="absolute w-full h-full transition-transform duration-500 ease-in-out"
            style={{ transform: `translateX(-${currentIndex * 100}%)` }}
          >
            {images.map((image, index) => (
              <div
                key={index}
                className="absolute w-full h-full"
                style={{ left: `${index * 100}%` }}
              >
                <img
                  src={image.url}
                  alt={image.title}
                  className="w-full h-full object-cover"
                  onError={(e) => {
                    e.target.src =
                      "https://images.unsplash.com/photo-1682687220566-5599dbbebf11";
                  }}
                />
              </div>
            ))}
          </div>

          <button
            onClick={() => handleSlideChange(setCurrentIndex, images.length)("prev")}
            className="absolute left-4 top-1/2 -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-75 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            aria-label="Previous slide"
          >
            <FaChevronLeft size={24} />
          </button>

          <button
            onClick={() => handleSlideChange(setCurrentIndex, images.length)("next")}
            className="absolute right-4 top-1/2 -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-75 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            aria-label="Next slide"
          >
            <FaChevronRight size={24} />
          </button>

          <div
            className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex justify-center gap-2"
            role="tablist"
          >
            {images.map((_, index) => (
              <button
                key={index}
                onClick={() => setCurrentIndex(index)}
                className={`w-3 h-3 rounded-full focus:outline-none transition-all duration-300 ${
                  currentIndex === index
                    ? "bg-white scale-125"
                    : "bg-white bg-opacity-50 hover:bg-opacity-75"
                }`}
                role="tab"
                aria-selected={currentIndex === index}
                aria-label={`Go to slide ${index + 1}`}
              />
            ))}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="mt-32 mx-[8.5em]">
      <h1 className="text-4xl mb-2 font-semibold font-cairo text-black">
        Game Development
      </h1>
      <div className="bg-gray-300 h-0.5 w-100vw"></div>
      <h1 className="text-3xl font-cairo font-medium mt-8 mb-10">Upcoming Games</h1>
      <div className="grid grid-cols-2">
        {renderCarousel(images1, currentIndex1, setCurrentIndex1)}
        <div>
          <h1 className="text-3xl font-cairo font-semibold mb-4">Bubble Fish</h1>
          <p>
            Description for Bubble Fish. Lorem ipsum dolor sit amet consectetur
            adipisicing elit.
          </p>
        </div>
      </div>
      <div className="grid grid-cols-2 mt-10">
        {renderCarousel(images2, currentIndex2, setCurrentIndex2)}
        <div>
          <h1 className="text-3xl font-cairo font-semibold mb-4">Volcano Run</h1>
          <p>
            Description for Volcano Run. Lorem ipsum dolor sit amet consectetur
            adipisicing elit.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Game;
