import React, { useState, useRef } from "react";
import walkthrough from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/walkthrough.jpg';  
import interior from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/interior.jpg';
import creative from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/creative.jpg';
import graphic from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/graphic.jpg';
import game from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/game.jpg';
import motion from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/motion.jpg';
import digital from 'C:/Users/PC/Desktop/Abhi/Cinegraphics-main/frontend/src/assets/services/digital.jpg';

const dummyProducts = [
  {
    id: 1,
    title: "Walkthrough & Cinematics",
    image: walkthrough
  },
  {
    id: 2,
    title: "Interior Design",
    image: interior
  },
  {
    id: 3,
    title: "Creative Visualization",
    image: creative
  },
  {
    id: 4,
    title: "Graphic Design",
    image: graphic
  },
  {
    id: 5,
    title: "Game Development",
    image: game
  },
  {
    id: 6,
    title: "Motion Graphics",
    image: motion
  },
  {
    id: 7,
    title: "Digital Marketing",
    image: digital
  }
];

const ProductCard = ({ product }) => {
  return (
    <div className="flex-shrink-0 w-72 bg-white rounded-lg overflow-visible transform transition-all duration-300 hover:scale-105 mx-4">
      <div className="relative h-64 overflow-hidden">
        <img
          src={product.image}
          alt={product.title}
          className="w-full h-full object-cover transition-transform duration-300 hover:scale-110"
          onError={(e) => {
            e.target.src = "https://images.unsplash.com/photo-1590212151175-e58edd96185b";
          }}
        />
      </div>
      <div className="p-4">
        <h3 className="text-xl font-semibold text-gray-800 mb-2">{product.title}</h3>
        <div className="flex justify-between items-center">
          <button className="px-4 bg-primary-orange text-white hover:bg-orange-500 transition-colors duration-300">
            Explore
          </button>
        </div>
      </div>
    </div>
  );
};

const Services = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);
  const containerRef = useRef(null);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.pageX - containerRef.current.offsetLeft);
    setScrollLeft(containerRef.current.scrollLeft);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    const x = e.pageX - containerRef.current.offsetLeft;
    const walk = (x - startX) * 2; // Adjust scroll speed
    containerRef.current.scrollLeft = scrollLeft - walk;
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleMouseLeave = () => {
    setIsDragging(false);
  };

  return (
    <div className="relative overflow-hidden bg-gray-100 py-8">
      <div className="container ml-[8.5em]">
        <h2 className="services-heading text-4xl font-nunito text-center text-black mb-24">OUR SERVICES</h2>
        <div
          ref={containerRef}
          className="relative overflow-hidden cursor-grab active:cursor-grabbing"
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          onMouseLeave={handleMouseLeave}
        >
          <div className="flex">
            {dummyProducts.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Services;
