import React, { useState, useEffect } from "react";
import { FaFacebookF, FaTwitter, FaInstagram, FaLinkedinIn } from "react-icons/fa";
import { FiMail, FiPhone, FiMapPin } from "react-icons/fi";
import images from '../../constants/images';
import "./Footer.css";

const Footer = () => {
  const [isVisible, setIsVisible] = useState(false);

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  useEffect(() => {
    const toggleVisibility = () => {
      if (window.pageYOffset > 300) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };

    window.addEventListener("scroll", toggleVisibility);
    return () => window.removeEventListener("scroll", toggleVisibility);
  }, []);

  const scrollToSection = (id) => {
    const section = document.querySelector(id);
    const offset = 80; // Adjust this value based on your header height
    const elementPosition = section?.offsetTop || 0;
    const offsetPosition = elementPosition - offset;

    window.scrollTo({
      top: offsetPosition,
      behavior: "smooth",
    });
  };

  return (
    <footer className="bg-white text-black pt-16 pb-8 px-2">
      <div className="container mx-auto px-3">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {/* Company Info */}
          <div className="ml-12">
            <img
              src={images.sreematha}
              alt="RealEstate Logo"
              className="h-20 w-auto"
            />
          </div>

          {/* Quick Links */}
          <div className="ml-32">
            <h3 className="text-xl font-semibold mb-4">Quick Links</h3>
            <ul className="space-y-2 ml-0">
              {[
                { name: "Home", id: "#home" },
                { name: "Projects", id: "#projects" },
                { name: "About Us", id: "#aboutus" },
                { name: "Contact Us", id: "#contact" },
              ].map((link) => (
                <li key={link.name}>
                  <button
                    className="text-gray-600 hover:text-white transition-colors duration-300"
                    aria-label={`Navigate to ${link.name} page`}
                    onClick={() => scrollToSection(link.id)}
                  >
                    {link.name}
                  </button>
                </li>
              ))}
            </ul>
          </div>

          {/* Contact Info */}
          <div className="contactinfo">
            <h3 className="text-xl font-semibold mb-4">Contact Us</h3>
            <div className="contactdet space-y-4">
              <div className="flex items-center space-x-3">
                <FiPhone className="text-primary-500" />
                <span className="text-gray-600">+91 999999999</span>
              </div>
              <div className="flex items-center space-x-3">
                <FiMail className="text-primary-500" />
                <span className="text-gray-600">info@srimaathainfradevelopers.com</span>
              </div>
              <div className="flex items-center space-x-3">
                <FiMapPin className="text-primary-500" />
                <span className="text-gray-600">L.B.Nagar, Hyderabad</span>
              </div>
            </div>
          </div>
        </div>

        {/* Social Links */}
        <div className="icons flex flex-row-reverse gap-4 mt-0 mr-[20.2rem]">
          {[
            { Icon: FaFacebookF, label: "Facebook" },
            { Icon: FaTwitter, label: "Twitter" },
            { Icon: FaInstagram, label: "Instagram" },
            { Icon: FaLinkedinIn, label: "LinkedIn" },
          ].map(({ Icon, label }) => (
            <button
              key={label}
              className="iconsa bg-gray-800 flex items-center justify-center hover:bg-primary-orange transition-colors duration-300"
              aria-label={`Visit our ${label} page`}
            >
              <Icon className="text-white" />
            </button>
          ))}
        </div>

        <div className="mt-6 pt-8 border-t border-gray-800">
          <div className="flex md:flex-row justify-between items-center">
            <div className="text-gray-400 text-sm">
              Â© {new Date().getFullYear()} Sri Maatha. All rights reserved.
            </div>
            <div className="text-gray-400 text-sm">
              Developed by Cinegraphic Renders.
            </div>
          </div>
        </div>
      </div>



    </footer>
  );
};

export default Footer;
