import React, { useState, useEffect } from "react";
import { FaShoppingCart } from "react-icons/fa";

const dummyProducts = [
  {
    id: 1,
    title: "Premium Wireless Headphones",
    price: "$299.99",
    description: "High-quality wireless headphones with noise cancellation",
    image: "https://images.unsplash.com/photo-1505740420928-5e560c06d30e"
  },
  {
    id: 2,
    title: "Smart Watch Pro",
    price: "$199.99",
    description: "Advanced fitness tracking with heart rate monitoring",
    image: "https://images.unsplash.com/photo-1523275335684-37898b6baf30"
  },
  {
    id: 3,
    title: "4K Digital Camera",
    price: "$899.99",
    description: "Professional-grade camera with advanced features",
    image: "https://images.unsplash.com/photo-1526170375885-4d8ecf77b99f"
  },
  {
    id: 4,
    title: "Gaming Laptop",
    price: "$1299.99",
    description: "High-performance laptop for ultimate gaming experience",
    image: "https://images.unsplash.com/photo-1593642632823-8f785ba67e45"
  },
  {
    id: 5,
    title: "Wireless Speaker",
    price: "$149.99",
    description: "Premium sound quality with deep bass response",
    image: "https://images.unsplash.com/photo-1608043152269-423dbba4e7e1"
  },
  {
    id: 6,
    title: "Smartphone X",
    price: "$799.99",
    description: "Latest flagship phone with advanced camera system",
    image: "https://images.unsplash.com/photo-1511707171634-5f897ff02aa9"
  },
  {
    id: 7,
    title: "Tablet Pro",
    price: "$599.99",
    description: "Versatile tablet for work and entertainment",
    image: "https://images.unsplash.com/photo-1585790050230-5dd28404ccb9"
  }
];

const ProductCard = ({ product }) => {
  return (
    <div className="flex-shrink-0 w-72 bg-white rounded-lg shadow-lg overflow-hidden transform transition-all duration-300 hover:scale-105 hover:shadow-xl mx-4">
      <div className="relative h-64 overflow-hidden">
        <img
          src={product.image}
          alt={product.title}
          className="w-full h-full object-cover transition-transform duration-300 hover:scale-110"
          onError={(e) => {
            e.target.src = "https://images.unsplash.com/photo-1590212151175-e58edd96185b";
          }}
        />
      </div>
      <div className="p-4">
        <h3 className="text-xl font-semibold text-gray-800 mb-2">{product.title}</h3>
        <p className="text-gray-600 text-sm mb-2">{product.description}</p>
        <div className="flex justify-between items-center">
          <span className="text-2xl font-bold text-blue-600">{product.price}</span>
          <button className="p-2 rounded-full bg-blue-500 text-white hover:bg-blue-600 transition-colors duration-300">
            <FaShoppingCart size={20} />
          </button>
        </div>
      </div>
    </div>
  );
};

const Services = () => {
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setScrollPosition((prevPosition) => {
        const newPosition = prevPosition + 1;
        return newPosition >= dummyProducts.length ? 0 : newPosition;
      });
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="relative max-w-full overflow-hidden bg-gray-100 py-8">
      <div className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-8">Featured Products</h2>
        <div className="relative overflow-hidden">
          <div
            className="flex transition-transform duration-500 ease-in-out"
            style={{
              transform: `translateX(-${scrollPosition * 288}px)`
            }}
          >
            {dummyProducts.map((product) => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Services;
