import React, { useState } from "react";

import './Courses.css';
import gallery1 from '../../assets/gallery 1.jpeg';


const Courses = () => {


  return (
    <div className="courses mt-24 pt-10 w-full bg-gradient-to-br from-gray-50 to-gray-100  " id="courses">
      <div >
        <h1 className="course-heading mb-24 font-nunito text-black text-center gap-4  ">
          OUR COURSES        
        </h1>
      </div>

    <div className="flex">
      <div className="course-image ml-[8.5em] mr-16 pb-24">       
        <img src={gallery1} alt="unreal course" />       
      </div>
      <div>
        <h1 className="course-name font-nunito font-medium">Unreal Kickstart:</h1>
        <h2 className="course-subhead font-nunito text-size-2xl">Begin your unreal journey</h2>
        <p className="font-nunito text-size-xl py-14">Get Started in the world's most exciting realtime tool.</p>
        <p className="font-nunito text-size-2xl ">PRICE: 250 Rs.</p>
        <button className="bg-primary-orange hover:bg-orange-600 text-white w-24 h-10 mt-4">Enroll Now</button>
      </div>
    </div>

    </div>
  );
};

export default Courses;
