import React from "react";
import './Projects.css';
import { FiExternalLink } from "react-icons/fi";
import project1 from '../../assets/project1.png';
import project2 from '../../assets/project2.png';
import project3 from '../../assets/project3.png';
import project4 from '../../assets/project4.png';
import project5 from '../../assets/project5.png';
import project6 from '../../assets/project6.png';

const ImageCard = ({ image, title }) => {
  return (
    <div className="group relative overflow-hidden rounded-lg cursor-pointer transition-transform duration-300 hover:scale-[1.02] h-full">

      <img
        src={image}
        alt={title}
        className="w-full h-full object-cover"
        loading="lazy"
      />
      <div className="absolute inset-0 bg-black bg-opacity-40 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
        <div className="absolute top-4 left-4">
          <h3 className="text-white text-xl font-semibold">{title}</h3>
        </div>
        <button className="absolute bottom-4 right-4 bg-primary-orange text-white px-4 py-2 rounded-full flex items-center gap-2 transform translate-y-full group-hover:translate-y-0 transition-transform duration-300">
          <span>View</span>

        </button>
      </div>
    </div>
  );
};

const Projects = () => {
  const galleryData = [
    {
      id: 1,
      image: project1,
      title: "Project1"
    },
    {
      id: 2,
      image: project2,
      title: "Project1"
    },
    {
      id: 3,
      image: project3,
      title: "Project1"
    },
    {
      id: 4,
      image: project4,
      title: "Project1"
    },
    {
      id: 5,
      image: project5,
      title: "Project1"
    },
    {
      id: 6,
      image: project6,
      title: "Project1"
    }
  ];

  return (
    <>
    <h1 className="projects-heading ">
      OUR PROJECTS
    </h1>
    <div className="container mx-auto px-4 py-8">

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 h-screen">
        <div className="lg:col-span-2 lg:row-span-2">
          <ImageCard {...galleryData[0]} />
        </div>
        <div>
          <ImageCard {...galleryData[1]} />
        </div>
        <div>
          <ImageCard {...galleryData[2]} />
        </div>
        <div>
          <ImageCard {...galleryData[3]} />
        </div>
        <div>
          <ImageCard {...galleryData[4]} />
        </div>
        <div>
          <ImageCard {...galleryData[5]} />
        </div>
      </div>
    </div>
    </>
  );
};

export default Projects;
