import React from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import "./Projects.css";
import project1 from "../../assets/project1.png";
import project2 from "../../assets/project2.png";
import project3 from "../../assets/project3.png";
import project4 from "../../assets/project4.png";
import project5 from "../../assets/project5.png";
import project6 from "../../assets/project6.png";

const ImageCard = ({ image, title }) => {
  return (
    <div className="group relative overflow-hidden cursor-pointer transition-transform duration-300 hover:scale-[1.02] h-full">
      <img
        src={image}
        alt={title}
        className="w-full h-full object-cover"
        loading="lazy"
      />
      <div className="absolute inset-0 bg-black bg-opacity-40 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
        <div className="absolute top-4 left-4">
          <h3 className="text-white text-xl font-cairo font-semibold">{title}</h3>
        </div>
        <button className="absolute font-cairo rounded-lg bottom-4 right-4 bg-primary-orange hover:bg-orange-600 text-white px-4 py-2 flex items-center gap-2 transform translate-y-full group-hover:translate-y-0 transition-transform duration-300">
          <span>View</span>
        </button>
      </div>
    </div>
  );
};

const Projects = () => {
  const navigate = useNavigate(); // Initialize navigate
  const galleryData = [
    { id: 1, image: project1, title: "Title" },
    { id: 2, image: project2, title: "Title" },
    { id: 3, image: project3, title: "Title" },
    { id: 4, image: project4, title: "Title" },
    { id: 5, image: project5, title: "Title" },
    { id: 6, image: project6, title: "Title" },
  ];

  return (
    <>
      <h1 className="projects-heading font-cairo">Our Projects</h1>
      <div className="project-container px-4 py-8">
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 h-screen">
          <div className="lg:col-span-2 rounded-xl lg:row-span-2">
            <ImageCard {...galleryData[0]} />
          </div>
          {galleryData.slice(1).map((data) => (
            <div key={data.id}>
              <ImageCard {...data} />
            </div>
          ))}
        </div>
      </div>
      <div className="flex justify-center mt-8 mr-32">
        <button
          onClick={() => navigate("/Projectpage")} // Navigate to ProjectsPage
          className="all-projects-btn rounded-lg font-cairo bg-primary-orange text-white h-10 w-32 hover:bg-orange-600"
        >
          All Projects
        </button>
      </div>
    </>
  );
};

export default Projects;
