import React, { useState } from "react";

import './Courses.css';
import gallery1 from '../../assets/gallery 1.jpeg';


const Courses = () => {
  const [loading, setLoading] = useState(false);

  const cardData = [
    {
      id: 1,
      title: "Gallery 1",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
      image: gallery1,
      cta: "Explore"
    },
    {
      id: 2,
      title: "Gallery 2",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
      image: gallery2,
      cta: "Explore"
    },
    {
      id: 3,
      title: "Gallery 3",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
      image: gallery3,
      cta: "Explore"
    }
  ];

  const handleClick = () => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 1500);
  };

  return (
    <div className="gallery w-full bg-gradient-to-br from-gray-50 to-gray-100  " id="gallery">
      <div className="mb-10 text-center md:text-left">
        <h1 className="text font-bold text-primary-blue gap-4  ">
          Gallery
          <img src={arrow2} alt="arrow" className="arrow " />
        </h1>
      </div>
      <div className="max-w-8xl mx-auto">
        {loading && (
          <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center z-50">
            <BiLoaderAlt className="animate-spin text-white text-4xl" />
          </div>
        )}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 md:gap-8">
          {cardData.map((card) => (
            <div
              key={card.id}
              className="bg-white rounded-2xl shadow-md overflow-hidden transition-transform duration-300 hover:shadow-lg hover:-translate-y-2 group"
              role="article"
            >
              <div className="flex flex-col h-full">
                <div className="w-full px-4 pt-0 h-56 sm:h-64 md:h-72 relative overflow-hidden">
                  <img
                    src={card.image}
                    alt={card.title}
                    className="w-full rounded-lg h-full object-cover transform group-hover:scale-105 transition-transform duration-300"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                </div>
                <div className="p-4  flex flex-col justify-between flex-grow">
                  <div>
                    <h2
                      className="text-lg md:text-xl font-bold text-primary-blue mb-2 group-hover:text-primary-orange transition-colors duration-200"
                      id={`card-title-${card.id}`}
                    >
                      {card.title}
                    </h2>
                    <p
                      className="text-gray-600  text-sm md:text-base mb-4 line-clamp-3"
                      id={`card-desc-${card.id}`}
                    >
                      {card.description}
                    </p>
                  </div>
                  <button
                    onClick={handleClick}
                    className="inline-flex items-center justify-center px-4 py-2 md:px-6 md:py-3 rounded-full text-sm md:text-base font-medium text-white bg-gradient-to-r from-primary-blue to-primary-blue hover:from-primary-orange hover:to-primary-orange transition-all duration-200 hover:scale-105 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    aria-labelledby={`card-title-${card.id}`}
                    aria-describedby={`card-desc-${card.id}`}
                  >
                    {card.cta}
                    <FiArrowRight className="ml-2 group-hover:translate-x-1 transition-transform duration-200" aria-hidden="true" />
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Courses;
