import React from 'react';
import './Header.css';
import { FiArrowRight } from "react-icons/fi";
import banner from '../../assets/banner.png';

const Header = () => (
  <div className="bg-white" id="home">
    <div className="banner-content">
      <img src={banner} className='h-[100vh] w-auto'/>
      <div className="banner-text">
        <h1 className=" font-bold mb-2 leading-tight ">
          Carefully Selected and Curated
        </h1>
        <button
          type="button"
          className="btn focus:outline-none text-black bg-primary-orange hover:bg-yellow-400 font-medium rounded-lg flex gap-0.4 pl-5 py-2.5 me-2 mb-2"
          onClick={() => {
            const contactSection = document.querySelector('#contact');
            contactSection?.scrollIntoView({ behavior: 'smooth' });
          }}
        >
          Make An Enquiry
          <FiArrowRight className="ml-2 arr" aria-hidden="true" />
        </button>
      </div>
    </div>
  </div>
);

export default Header;
