import React, { useState } from "react";
import { FaFacebook, FaInstagram} from "react-icons/fa";
import { FaXTwitter } from "react-icons/fa6";
import { FiArrowRight } from "react-icons/fi";
import { BiLoaderAlt } from "react-icons/bi";
import './About.css';
import arrow2 from '../../assets/arrow2.png';
import founder1 from '../../assets/founder1.png';
import founder2 from '../../assets/founder2.png';
import founder3 from '../../assets/founder3.png';


const About = () => {
  const [loading, setLoading] = useState(false);

  const cardData = [
    {
      id: 1,
      title: "D Venkat",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
      image: founder1,
      socials: {
        linkedin: "https://linkedin.com",
        twitter: "https://twitter.com",
        github: "https://github.com"
      }

    },
    {
      id: 2,
      title: "M Sunil Reddy",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
      image: founder2,
      socials: {
        linkedin: "https://linkedin.com",
        twitter: "https://twitter.com",
        github: "https://github.com"
      }

    },
    {
      id: 3,
      title: "K Naveen Kumar",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
      image: founder3,
      socials: {
        linkedin: "https://linkedin.com",
        twitter: "https://twitter.com",
        github: "https://github.com"
      }

    }
  ];

  const handleClick = () => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 1500);
  };

  return (
    <div className="gallery w-full mb-10 mt-10 bg-white  " id="aboutus">
      <div className="mb-10 text-center md:text-left">
        <h1 className="text font-bold text-primary-blue gap-4  ">
          About Us
          <img src={arrow2} alt="arrow" className="arrowa  " />
        </h1>
      </div>
      <div className="max-w-8xl mx-auto">
        {loading && (
          <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center z-50">
            <BiLoaderAlt className="animate-spin text-white text-4xl" />
          </div>
        )}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 md:gap-8">
          {cardData.map((card) => (
            <div
              key={card.id}
              className="bg-white rounded-2xl shadow-md overflow-hidden transition-transform duration-300 hover:shadow-lg hover:-translate-y-2 group"
              role="article"
            >
              <div className="flex flex-col h-50vh">
                <div className="w-full px-4 pt-0 h-56vh sm:h-64 md:h-72 relative overflow-hidden">
                  <img
                    src={card.image}
                    alt={card.title}
                    className="w-full rounded-lg h-56vh object-cover transform group-hover:scale-105 transition-transform duration-300"
                  />

                </div>
                <div className="p-6  flex flex-col justify-between flex-grow">
                  <div>
                    <h2
                      className="text-lg md:text-xl font-bold text-primary-blue mb-2 "
                      id={`card-title-${card.id}`}
                    >
                      {card.title}
                    </h2>
                    <p
                      className="text-gray-600  text-sm md:text-base mb-4 line-clamp-3"
                      id={`card-desc-${card.id}`}
                    >
                      {card.description}
                    </p>
                    <div className="flex space-x-4">
                  <a
                    href={card.socials.linkedin}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-gray-600 hover:text-primary-orange transition-colors duration-300"
                    aria-label={`${card.name}'s LinkedIn profile`}
                  >
                    <FaFacebook size={24} />
                  </a>
                  <a
                    href={card.socials.twitter}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-gray-600 hover:text-primary-orange transition-colors duration-300"
                    aria-label={`${card.name}'s Twitter profile`}
                  >
                    <FaXTwitter size={24} />
                  </a>
                  <a
                    href={card.socials.github}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-gray-600 hover:text-primary-orange transition-colors duration-300"
                    aria-label={`${card.name}'s GitHub profile`}
                  >
                    <FaInstagram size={24} />
                  </a>
                </div>
                  </div>

                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default About;
