import React from "react";

const ProjectsPage = () => {
  const projects = [
    {
      id: 1,
      title: "E-Commerce Platform",
      description: "A full-featured e-commerce platform built with React and Node.js, featuring product management, cart functionality, and secure payment integration.",
      image: "https://images.unsplash.com/photo-1557821552-17105176677c",
      githubLink: "https://github.com/project1",
      liveLink: "https://project1.com"
    },
    {
      id: 2,
      title: "Task Management System",
      description: "A comprehensive task management application with real-time updates, team collaboration features, and progress tracking capabilities.",
      image: "https://images.unsplash.com/photo-1454165804606-c3d57bc86b40",
      githubLink: "https://github.com/project2",
      liveLink: "https://project2.com"
    },
    {
      id: 3,
      title: "Social Media Dashboard",
      description: "An analytics dashboard for social media management, providing insights and metrics visualization for multiple platforms.",
      image: "https://images.unsplash.com/photo-1460925895917-afdab827c52f",
      githubLink: "https://github.com/project3",
      liveLink: "https://project3.com"
    },
    {
      id: 4,
      title: "Weather Forecast App",
      description: "A weather application that provides real-time forecasts, interactive maps, and detailed weather information for locations worldwide.",
      image: "https://images.unsplash.com/photo-1504608524841-42fe6f032b4b",
      githubLink: "https://github.com/project4",
      liveLink: "https://project4.com"
    },
    {
      id: 5,
      title: "Portfolio Generator",
      description: "A tool that helps developers create and customize their professional portfolios with various templates and styling options.",
      image: "https://images.unsplash.com/photo-1461749280684-dccba630e2f6",
      githubLink: "https://github.com/project5",
      liveLink: "https://project5.com"
    },
    {
      id: 6,
      title: "Recipe Finder",
      description: "A recipe discovery platform with filtering capabilities, nutritional information, and meal planning features.",
      image: "https://images.unsplash.com/photo-1466637574441-749b8f19452f",
      githubLink: "https://github.com/project6",
      liveLink: "https://project6.com"
    }
  ];

  const ProjectItem = ({ project }) => (
    <div className="bg-white rounded-lg shadow-xl overflow-hidden transition-transform duration-300 hover:scale-[1.02] mb-8">
      <div className="md:flex h-[400px]">
        <div className="md:w-1/3">
          <img
            src={project.image}
            alt={project.title}
            className="h-full w-full object-cover"
            onError={(e) => {
              e.target.src = "https://images.unsplash.com/photo-1516259762381-22954d7d3ad2";
            }}
          />
        </div>
        <div className="md:w-2/3 p-6">
          <h2 className="text-2xl font-bold text-gray-800 mb-3">{project.title}</h2>
          <p className="text-gray-600">{project.description}</p>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100 py-12">
      <div className="mx-auto px-8 sm:px-16 md:px-24 lg:px-32 xl:px-40 max-w-[1920px]">
        <h1 className="text-4xl font-bold text-gray-800 mb-8">My Projects</h1>
        <div className="space-y-8">
          {projects.map((project) => (
            <ProjectItem key={project.id} project={project} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProjectsPage;
