import React, { useRef, useEffect } from "react";
import walkthrough from "./assets/services/walkthrough.jpg";
import interior from "./assets/services/interior.jpg";
import creative from "./assets/services/creative.jpg";
import graphic from "./assets/services/graphic.jpg";
import game from "./assets/services/game.jpg";
import motion from "./assets/services/motion.jpg";
import digital from "./assets/services/digital.jpg";

const dummyProducts = [
  { id: 1, title: "Walkthrough & Cinematics", image: walkthrough, link: "/walkthrough" },
  { id: 2, title: "Interior Design", image: interior, link: "/interior" },
  { id: 3, title: "Creative Visualization", image: creative, link: "/creative" },
  { id: 4, title: "Graphic Design", image: graphic },
  { id: 5, title: "Game Development", image: game, link: "/game" },
  { id: 6, title: "Motion Graphics", image: motion },
  { id: 7, title: "Digital Marketing", image: digital },
];

const ProductCard = ({ product }) => {
  return (
    <div className="flex-shrink-0 pt-10 w-96 bg-transparent rounded-lg overflow-visible transform transition-all duration-300 hover:scale-105 mx-2 pb-14">
      <div className="relative h-96 rounded-lg overflow-hidden">
        <img
          src={product.image}
          alt={product.title}
          className="w-full h-full rounded-lg object-cover transition-transform duration-300 hover:scale-110"
          onError={(e) => {
            e.target.src = "https://images.unsplash.com/photo-1590212151175-e58edd96185b";
          }}
        />
        <div className="absolute inset-0 pl-6 bg-black bg-opacity-30 pb-6 flex flex-col justify-end items-start text-center">
          <h3 className="text-2xl font-semibold text-white font-cairo mb-4">{product.title}</h3>
          {product.link && (
            <a
              href={product.link}
              className="px-4 py-1 font-cairo bg-primary-orange rounded-lg text-white hover:bg-orange-500 transition-colors duration-300"
            >
              Explore
            </a>
          )}
        </div>
      </div>
    </div>
  );
};

const Services = ({ servicesRef }) => {
  const containerRef = useRef(null);
  const autoScrollRef = useRef();

  const CARD_WIDTH = 288;
  const SCROLL_AMOUNT = CARD_WIDTH * 2;

  const startAutoScroll = () => {
    autoScrollRef.current = setInterval(() => {
      if (containerRef.current) {
        const container = containerRef.current;

        container.scrollTo({
          left: container.scrollLeft + SCROLL_AMOUNT,
          behavior: "smooth",
        });

        if (container.scrollLeft >= container.scrollWidth / 2) {
          setTimeout(() => {
            container.scrollLeft = 0;
          }, 500);
        }
      }
    }, 4000);
  };

  useEffect(() => {
    startAutoScroll();
    return () => clearInterval(autoScrollRef.current);
  }, []);

  return (
    <div ref={servicesRef} className="relative overflow-hidden bg-gray-100 py-8">
      <div className="w-[85vw] ml-[9em]">
        <h2 className="services-heading text-5xl font-semibold text-center text-black mb-10 font-cairo">Our Services</h2>
        <div ref={containerRef} className="relative rounded-xl flex overflow-hidden">
          <div className="flex rounded-lg">
            {dummyProducts.concat(dummyProducts).map((product, index) => (
              <ProductCard key={index} product={product} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Services;
