import React from "react";
import { FiExternalLink } from "react-icons/fi";

const ImageCard = ({ image, title }) => {
  return (
    <div className="group relative overflow-hidden rounded-lg cursor-pointer transition-transform duration-300 hover:scale-[1.02] h-full">

      <img
        src={image}
        alt={title}
        className="w-full h-full object-cover"
        loading="lazy"
      />
      <div className="absolute inset-0 bg-black bg-opacity-40 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
        <div className="absolute top-4 left-4">
          <h3 className="text-white text-xl font-semibold">{title}</h3>
        </div>
        <button className="absolute bottom-4 right-4 bg-white text-black px-4 py-2 rounded-full flex items-center gap-2 transform translate-y-full group-hover:translate-y-0 transition-transform duration-300">
          <span>View</span>
          <FiExternalLink className="w-4 h-4" />
        </button>
      </div>
    </div>
  );
};

const Projects = () => {
  const galleryData = [
    {
      id: 1,
      image: "https://images.unsplash.com/photo-1682687220742-aba13b6e50ba",
      title: "Nature Escape"
    },
    {
      id: 2,
      image: "https://images.unsplash.com/photo-1682687220063-4742bd7446a7",
      title: "Urban Life"
    },
    {
      id: 3,
      image: "https://images.unsplash.com/photo-1682687221073-53ad74c2cad7",
      title: "Mountain View"
    },
    {
      id: 4,
      image: "https://images.unsplash.com/photo-1682687220199-d0124f48f95b",
      title: "Ocean Waves"
    },
    {
      id: 5,
      image: "https://images.unsplash.com/photo-1682687220067-dced0c5bf699",
      title: "Desert Sunset"
    },
    {
      id: 6,
      image: "https://images.unsplash.com/photo-1682687220039-b2a2bb0cd25c",
      title: "Forest Path"
    }
  ];

  return (
    <>
    <h1 className="projects-heading">
      OUR PROJECTS
    </h1>
    <div className="container mx-auto px-4 py-8">

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 h-screen">
        <div className="lg:col-span-2 lg:row-span-2">
          <ImageCard {...galleryData[0]} />
        </div>
        <div>
          <ImageCard {...galleryData[1]} />
        </div>
        <div>
          <ImageCard {...galleryData[2]} />
        </div>
        <div>
          <ImageCard {...galleryData[3]} />
        </div>
        <div>
          <ImageCard {...galleryData[4]} />
        </div>
        <div>
          <ImageCard {...galleryData[5]} />
        </div>
      </div>
    </div>
    </>
  );
};

export default Projects;
